<div class="side-box">    <?php        $args = array(            'orderby' => 'date',            'order' => 'DESC',            'posts_per_page' => 5        );        $my_query = new WP_Query($args);        if( $my_query->have_posts() ) {    ?>        <h4 class="box-title">最新文章<a href="#" class="more">更多&gt;</a></h4>        <ul class="box-list">            <?php while ($my_query->have_posts()) : $my_query->the_post(); ?>                <li>                    <a href="<?php the_permalink(); ?>" target="_blank" class="side-item">                        <div class="item-img">                            <?php if(get_post_custom_values("thumbnail")){                                 $values = get_post_custom_values("thumbnail");                                 echo '<img src="'.$values[0].'" alt="">';                                } else {                                     echo '<img src="'.get_bloginfo('template_url').'/images/related-img.jpg">';                                 }                            ?>                        </div>                        <div class="item-info">                            <h4 class="item-title"><?php the_title();?></h4>                            <div class="item-ft">                                <span>阅读(<?php if(function_exists('the_views')) { the_views(); } else { echo '200';} ?>)</span>                                <span>评论(200)</span>                            </div>                        </div>                    </a>                </li>            <?php endwhile; ?>        </ul>    <?php  } wp_reset_query(); ?></div><div class="side-box">    <?php        $args = array(            'orderby' => 'comment_count',            'order' => 'DESC',            'posts_per_page' => 5        );        $my_query = new WP_Query($args);        if( $my_query->have_posts() ) {    ?>        <h4 class="box-title">评论排行榜<a href="#" class="more">更多&gt;</a></h4>        <ul class="box-list">